    static public Dictionary<string, Language> AllLanguages = new Dictionary<string, Language>();

    static SupportedLanguages() {

      foreach (Language language in LanguageList) {
        AllLanguages.Add(language.LanguageId, language);
      }
    }

    static public Dictionary<string, Language> GetLangauges(string LanguageGroupFilter = "") {

      var languages = new Dictionary<string, Language>();

      foreach (var language in AllLanguages) {
        if (LanguageGroupFilter.Equals("") || language.Value.LanguageGroup.Equals(LanguageGroupFilter)) {
          languages.Add(language.Key, language.Value);
        }
      }

      return languages;

    }

    static public Dictionary<string, Language> GetCommonLangauges() {

      var commonLanguages = new Dictionary<string, Language>();

      foreach (var language in AllLanguages) {
        if (!language.Value.ShownOnlyOnExtendedLanguageList)
          commonLanguages.Add(language.Key, language.Value);
      }

      return commonLanguages;

    }

    static public Language GetLanguageFromFullName(string FullName) {
      return AllLanguages.First(languageEntry => languageEntry.Value.FullName == FullName).Value;
    }

    static public string[] GetLanguageGroups() {
      var languageGroups = new List<string>();
      foreach (var language in AllLanguages.Values) {
        languageGroups.Add(language.LanguageGroup);
      }
      return languageGroups.Distinct().OrderBy(group => group).ToList().ToArray();
    }

